class AjaxTest extends PHPUnit_Framework_TestCase {

	public function testJSONencode() {
		$this->assertEquals(
			'{'.
				'"int":1,'.
				'"float":3.14,'.
				'"bool":true,'.
				'"null":null,'.
				'"string":"foo",'.
				'"func1":function test1() { return 1; },'.
				'"func2":function test2() { return 2; },'.
				'"func3":function test3() { return 3; }'.
			'}',
			Ajax::json_encode_jsfunc(array(
				'int' => 1,
				'float' => 3.14,
				'bool' => true,
				'null' => null,
				'string' => 'foo',
				'func1' => 'function test1() { return 1; }',
				'func2' => 'function test2() { return 2; }',
				'func3' => 'function test3() { return 3; }'
			))
		);
	}

}
